import React, { useState } from "react";
import styles from "./Dialogs.module.css";
import { NavLink } from "react-router-dom";
import { Route, Routes } from "react-router-dom";
import { DialogBase } from "./DialogBase";
import DialogItem from "./DialogItem";
import Message from "./Message";

export default function Dialogs() {
 const [activeId, setActive] = useState(0)
 const handleClick = (id) =>{
  setActive(id)
 } 


  return (
    <>
      <div className={styles.content}>
        <article className={styles.dialogsList}>
          {
            DialogBase.map
          }
          <DialogItem onClick={()=>handleClick(0)} dialog={DialogBase[0]} />
          <DialogItem onClick={()=>handleClick(1)} dialog={DialogBase[1]} />
          <DialogItem onClick={()=>handleClick(2)} dialog={DialogBase[2]} />
        </article>
        <Message dialog={DialogBase[activeId]}  />
      </div>
    </>
  );
}
